mapper.insert:
insert into temperature (id, temperature, time
  )
values (#{id,jdbcType=INTEGER}, #{temperature,jdbcType=DOUBLE}, #{time,jdbcType=VARCHAR}
  )

mapper.update:
update temperature
set temperature = #{temperature,jdbcType=DOUBLE},
  time = #{time,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select COUNT(*)

from temperature
where time between #{starttime,jdbcType=VARCHAR} and #{endtime,jdbcType=VARCHAR}
